CREATE DATABASE deviceData;
USE deviceData;

DROP TABLE user;
DROP TABLE device;
DROP TABLE data;

CREATE TABLE user (
userID INT NOT NULL AUTO_INCREMENT,
userName VARCHAR(255),
PRIMARY KEY (userID)
);

CREATE TABLE device (
deviceID INT NOT NULL AUTO_INCREMENT,
deviceName VARCHAR(255),
userID INT NOT NULL,
PRIMARY KEY (deviceID),
FOREIGN KEY  (userID) REFERENCES user(userID)
);
CREATE TABLE data (
deviceID INT,
pos VARCHAR(255),
temp INT,
humid INT,
hpa INT,
volt INT,
lte_rssi INT,
FOREIGN KEY (deviceID) REFERENCES device(deviceID)
);


